mapper.select:
SELECT bbsList.* FROM ( SELECT TA.ARTICLE_SEQ, TA.BBS_INFO_SEQ, (SELECT TBC.CATEGORY_NM FROM TB_BBS_CATEGORY TBC WHERE TBC.CATEGORY_CD = TA.CATEGORY_CD AND TBC.BBS_INFO_SEQ = TA.BBS_INFO_SEQ) AS CATEGORY_NM, TA.SUBJECT, TA.DEL_YN, TA.SECRET_YN, TA.NOTICE_YN, TA.USER_ID, TA.PRT_SEQ, TA.PRT_GRP, TA.BBS_ORDER, TA.INPT_IP, TA.UPD_IP, TA.READ_CNT, TA.INPT_USER_NAME, TA.INPT_USER_DEPT, DATE_FORMAT(TA.INPT_DATE, '%Y-%m-%d') AS INPT_DATE, TA.UPD_USER_NAME, UPD_DATE, TA.PUBLISH_YEAR, TA.PUBLISH_MONTH, (SELECT COUNT(COMMENT_SEQ) FROM TB_ARTICLE_COMMENTS TAC WHERE TAC.ARTICLE_SEQ = TA.ARTICLE_SEQ AND TAC.DEL_YN = 'N') AS COMMENT_CNT, (SELECT COUNT(FILE_SEQ) FROM TB_FILES TF WHERE TF.TABLE_SEQ = TA.ARTICLE_SEQ AND TF.TABLE_NM = 'TB_ARTICLES' ) AS FILE_CNT, (SELECT BBS_NM FROM TB_BBS_INFO TBI WHERE TBI.BBS_INFO_SEQ = TA.BBS_INFO_SEQ) AS BBS_NM, (SELECT BBS_CD FROM TB_BBS_INFO TBI WHERE TBI.BBS_INFO_SEQ = TA.BBS_INFO_SEQ) AS BBS_CD, ARTICLE_START_DATE, ARTICLE_END_DATE, DOC_SOURCE, TA.PUBLIC_RANGE, TA.PUBLIC_CYCLE, TA.PUBLIC_TIME, TA.PUBLIC_TYPE, TUD.NICKNAME as nickname FROM TB_ARTICLES TA INNER JOIN TB_USER_DETAIL TUD ON TA.INPT_SEQ = TUD.USER_SEQ WHERE 1 = 1 AND TA.BBS_INFO_SEQ =
#{bbs_info_seq}
(SELECT STBI.BBS_INFO_SEQ FROM TB_BBS_INFO STBI WHERE STBI.BBS_CD = #{bbs_cd})
) bbsList WHERE 1=1 ORDER BY bbsList.PRT_GRP DESC, bbsList.BBS_ORDER ASC, bbsList.ARTICLE_SEQ ASC LIMIT #{firstIndex}, #{recordCountPerPage}

SELECT TA.ARTICLE_SEQ, TA.BBS_INFO_SEQ, TA.CATEGORY_CD, (SELECT TBC.CATEGORY_NM FROM TB_BBS_CATEGORY TBC WHERE TBC.CATEGORY_CD = TA.CATEGORY_CD AND TBC.BBS_INFO_SEQ = TA.BBS_INFO_SEQ) AS CATEGORY_NM, TA.SUBJECT, TA.CONTENT, TA.DEL_YN, TA.SECRET_YN, TA.NOTICE_YN, TUL.USER_ID, TA.PRT_SEQ, TA.PRT_GRP, TA.BBS_ORDER, TA.INPT_IP, TA.UPD_IP, TA.READ_CNT, TA.INPT_USER_NAME, TA.INPT_SEQ, TA.INPT_USER_DEPT, DATE_FORMAT(TA.INPT_DATE, '%Y-%m-%d') AS INPT_DATE, TA.UPD_USER_NAME, TA.UPD_DATE, TBD.CATEGORY_USE_YN, TBD.ATTACH_FILE_USE_YN, DATE_FORMAT(TA.ARTICLE_START_DATE, '%Y-%m-%d') AS ARTICLE_START_DATE, DATE_FORMAT(TA.ARTICLE_END_DATE, '%Y-%m-%d') AS ARTICLE_END_DATE, TA.DOC_SOURCE, TA.EDITOR, TA.CATEGORIZE, TA.LINK_URL, TA.PUBLISH_YEAR, TA.PUBLISH_MONTH, TA.PUBLIC_RANGE, TA.PUBLIC_CYCLE, DATE_FORMAT(TA.PUBLIC_TIME, '%Y-%m-%d') AS PUBLIC_TIME, TA.PUBLIC_TYPE, TA.YOUTUBE_URL, TA.MANAGER, TUD.NICKNAME, TA.PLACE FROM TB_ARTICLES TA INNER JOIN TB_BBS_DETAIL TBD ON TA.BBS_INFO_SEQ = TBD.BBS_INFO_SEQ INNER JOIN TB_USER_DETAIL TUD ON TA.INPT_SEQ = TUD.USER_SEQ INNER JOIN TB_USER_LIST TUL ON TA.INPT_SEQ = TUL.USER_SEQ WHERE 1=1 AND TA.BBS_INFO_SEQ =
#{bbs_info_seq}
(SELECT STBI.BBS_INFO_SEQ FROM TB_BBS_INFO STBI WHERE STBI.BBS_CD = #{bbs_cd})
AND TA.ARTICLE_SEQ = #{article_seq}

mapper.update:
UPDATE TB_ARTICLES SET
PRT_GRP = #{article_seq}
WHERE ARTICLE_SEQ = #{article_seq}

UPDATE TB_ARTICLE_COMMENTS SET
PRT_GRP = #{comment_seq},
PRT_SEQ = #{comment_seq}
WHERE COMMENT_SEQ = #{comment_seq}

UPDATE TB_ARTICLES SET
    READ_CNT = READ_CNT+1
WHERE ARTICLE_SEQ = #{article_seq}

UPDATE TB_ARTICLES SET DEL_YN = 'Y', UPD_SEQ = #{upd_seq}, UPD_DATE = NOW() WHERE ARTICLE_SEQ = #{article_seq} AND BBS_INFO_SEQ =
#{bbs_info_seq}
(SELECT STBI.BBS_INFO_SEQ FROM TB_BBS_INFO STBI WHERE STBI.BBS_CD = #{bbs_cd})

UPDATE TB_ARTICLE_COMMENTS SET
    DEL_YN = #{del_yn},
    UPD_SEQ = #{upd_seq},
    UPD_DATE = NOW(),
    UPD_USER_TYPE = #{upd_user_type}
WHERE COMMENT_SEQ = #{comment_seq}
AND ARTICLE_SEQ = #{article_seq}

mapper.delete:
DELETE FROM TB_ARTICLES WHERE ARTICLE_SEQ = #{article_seq} AND BBS_INFO_SEQ =


        #{bbs_info_seq}


        (SELECT STBI.BBS_INFO_SEQ FROM TB_BBS_INFO STBI WHERE STBI.BBS_CD = #{bbs_cd})

DELETE FROM TB_ARTICLE_COMMENTS WHERE
ARTICLE_SEQ = #{article_seq}

    AND COMMENT_SEQ = #{comment_seq}
